mapper.insert:
INSERT INTO QNA_BOARD (
	QNA_NO
	, GST_ID
	, QNA_TITLE
	, QNA_CONTENT
	, QNA_DATE
	, QNA_DEL
	, QNA_REPLY_AT
) VALUES (
	#{qnaNo}
	, #{gstId}
	, #{qnaTitle}
	, #{qnaContent}
	, SYSDATE
	, 'N'
	, 'N'
)

mapper.update:
UPDATE QNA_BOARD
SET QNA_TITLE = #{qnaTitle}
	, QNA_CONTENT = #{qnaContent}
WHERE QNA_NO = #{qnaNo}

UPDATE QNA_BOARD
SET QNA_DEL = 'Y'
WHERE QNA_NO = #{qnaNo}
